<?if (!defined('FROM_INDEX')) go_to_error404_page();$data = get_sliders_data();$sliders_count = count($data);?><script type="text/javascript">	$(document).ready(		function() 		{			$("#sc2pef_lineTabs li a").each(				function(i) 				{					$("#sc2pef_lineTabs li a:eq("+i+")").click(						function()							{							var tab_id=i+1;							$("#sc2pef_lineTabs li a").removeClass("active");							$(this).addClass("active");							$("#slider_content .tabs").stop(false,false).hide();							$("#tab"+tab_id).stop(false,false).show();							return false;						}					)				}			)		}	)</script><ul id="sc2pef_lineTabs">		<?	if (!isset($_POST['location'])) $_POST['location'] =1;		for($i=1;$i<=5;$i++)	{		if($i==$_POST['location']) $_active=' class="active"';		else $_active='';				switch($i)		{			case 1: $location['_']='EU'; break;			case 2: $location['_']='US'; break;			case 3: $location['_']='CN'; break;			case 4: $location['_']='SEA'; break;			case 5: $location['_']='KR/TW'; break;		}				echo		('			<li><a href="#"'.$_active.'>Регион '.$location['_'].'</a></li>		');	}		unset($_location);	?>		</ul><div id="slider_content">	<?for ($i = 0; $i < $sliders_count; $i++){	if ($i == $_POST['current_slider_id']) $_active=' style="display: block;"';	else $_active='';		echo	('		<div id="tab'.$i.'"'.$_active.' class="tabs">			<div id="slider_content_text">	');}