<?function get_slider_data($slider_id){	$query = "SELECT * FROM sliders WHERE slider_id = '$slider_id' ORDER BY tab_index";	$query = mysql_query($query);	$data = get_norm_mysql_rows($query);    if ($data)    {        if ($slider_id == 1)            $path = SLIDER1_GALLERY_PATH;        else if ($slider_id == 2)            $path = SLIDER2_GALLERY_PATH;        for ($i = 0; $i < count($data); $i++)            $data[$i]['img'] = $path . $data[$i]['img'];    }    return $data;}?>